const inputData = $input.all().map(item => item.json.output).flat();

// Ensure inputData is an array
const dataArray = Array.isArray(inputData) ? inputData : [inputData];

// Function to split data into columns and skip empty rows
const splitData = (arr) => {
  return arr
    .map(item => {
      // Skip empty or header lines
      if (!item || item.includes("Full_Name") || item.includes("[empty]")) return null;

      // Split by '|' and trim spaces
      const parts = item.split("|").map(p => p.trim());

      // Skip row if email or GitHub URL is missing
      if (!parts[1] || !parts[2]) return null;

      return {
        fullName: parts[0] || "",
        email: parts[1] || "",
        githubUrl: parts[2] || "",
        profile_name: parts[3] || "" // e.g., "github url"
      };
    })
    .filter(Boolean); // Remove null entries
};

// Run the function
const result = splitData(dataArray);

// Return all items in n8n compatible format
return result.map(r => ({ json: r }));

// n8n Code Node (JavaScript)

// Get the input JSON (from previous node / webhook)
const input = items[0].json;

// Extract fields from body
const body = input.body;

// üîπ Helper function: Always return a clean comma-separated string
function normalizeToString(value) {
  if (Array.isArray(value)) {
    return value.join(", ");
  } else if (typeof value === "string") {
    // Handle comma or semicolon separated input gracefully
    return value.split(/[,;]+/).map(s => s.trim()).join(", ");
  } else if (value == null) {
    return "";
  } else {
    return String(value);
  }
}

// Flatten data into structured key-value pairs
return [
  {
    json: {
      // Company Details
      CompanyName: body.CompanyName,
      CompanyWebsite: body.CompanyWebsite,
      CompanyAddress: body.CompanyAddress,
      CompanyIndustry: body.CompanyIndustry,
      CompanyDescriptionAndMission: body.CompanyDescriptionAndMission,
      CompanyCultureAndValues: body.CompanyCultureAndValues,

      // HR Details
      fullName: body.fullName,
      hrTitle: body.hrTitle,
      hrEmail: body.hrEmail,
      hrPhone: body.hrPhone,
      hrLinkedIn: body.hrLinkedIn,
      preferredTimezone: body.preferredTimezone,

      // Job / Role Details
      PositionTitle: body.PositionTitle,
      Department: body.Department,
      JobType: body.JobType,
      WorkMode: body.WorkMode,
      Location: body.Location,
      ExperienceRequired: body.ExperienceRequired,
      ExpectedStartDate: body.ExpectedStartDate,
      HiringUrgency: body.HiringUrgency,
      Currency: body.Currency,
      MinimumSalary: body.MinimumSalary,
      MaximumSalary: body.MaximumSalary,
      EducationRequirements: body.EducationRequirements,

      // Skills & Responsibilities (safe normalization ‚úÖ)
      RequiredSkillsAndExperience: normalizeToString(body.RequiredSkillsAndExperience),
      KeyResponsibilities: normalizeToString(body.KeyResponsibilities),
      NiceToHaveSkills: normalizeToString(body.NiceToHaveSkills),
      BenefitsAndPerks: normalizeToString(body.BenefitsAndPerks),

      // Interview Process
      InterviewProcess: body.InterviewProcess
    }
  }
];

const results = [];

for (const item of $input.all()) {
    const data = item.json;

    if (data.items) {
        data.items.forEach(result => {
            const flatItem = {};

            // Top-level fields
            flatItem.title = result.title || '';
            flatItem.link = result.link || '';
            flatItem.snippet = result.snippet || '';
            flatItem.displayLink = result.displayLink || '';

            // Image
            flatItem.image = result.pagemap?.cse_image?.[0]?.src || '';

            // Parse first and last name from title
            if (flatItem.title.includes('-')) {
                const parts = flatItem.title.split('-');
                const namePart = parts[0].trim();
                const jobPart = parts.slice(1).join('-').trim();

                const nameParts = namePart.split(' ');
                flatItem.person_firstName = nameParts[0] || '';
                flatItem.person_lastName = nameParts.slice(1).join(' ') || '';
                flatItem.person_jobTitle = jobPart || '';
            } else {
                flatItem.person_firstName = '';
                flatItem.person_lastName = '';
                flatItem.person_jobTitle = '';
            }

            // Extract organization from snippet (optional heuristic)
            const orgMatch = flatItem.snippet.match(/at ([A-Za-z0-9\s]+)/);
            flatItem.organization = orgMatch ? orgMatch[1].trim() : '';

            results.push({ json: flatItem });
        });
    }
}

return results;

// Input: items from previous node
const seen = new Set();
const uniqueItems = [];

for (const item of items) {
    // Normalize first name: trim and lowercase
    const firstName = item.json.person_firstName.trim().toLowerCase();

    if (!seen.has(firstName)) {
        seen.add(firstName);
        uniqueItems.push(item); // keep original item
    }
}

// Return unique items
return uniqueItems;

