function parseTestPaperForGoogleForm(llmResponse) {
  try {
    // Extract raw string (from LLM response array)
    let rawOutput = llmResponse[0]?.output || "";

    // Remove unwanted wrappers like ```json, ```, and extra text before/after JSON
    let jsonStr = rawOutput
      .replace(/```json\s*|\s*```/g, '')   // remove code fences
      .trim();

    // Try to extract only the JSON part if extra text is around
    const firstBrace = jsonStr.indexOf("{");
    const lastBrace = jsonStr.lastIndexOf("}");
    if (firstBrace !== -1 && lastBrace !== -1) {
      jsonStr = jsonStr.substring(firstBrace, lastBrace + 1);
    }

    // Parse JSON safely
    const data = JSON.parse(jsonStr);

    // Validate required fields
    if (!data.testTitle || !data.difficulty || !Array.isArray(data.questions)) {
      throw new Error("Invalid input structure: Missing required fields (testTitle, difficulty, or questions)");
    }

    // Validate and filter questions
    const validQuestions = data.questions.filter(q =>
      typeof q.question === 'string' && q.question.trim() !== '' &&
      Array.isArray(q.options) && q.options.length > 0 && q.options.every(opt => typeof opt === 'string' && opt.trim() !== '') &&
      typeof q.correctAnswer === 'string' && q.correctAnswer.trim() !== ''
    );

    if (validQuestions.length === 0) {
      throw new Error("No valid questions found in input");
    }

    // Build Google Form payload
    const googleFormPayload = {
      info: {
        title: data.testTitle,
        documentTitle: data.testTitle,
        description: `A ${data.difficulty} level assessment for ${data.testTitle}`
      },
      items: validQuestions.map((question, index) => ({
        questionItem: {
          title: question.question,
          question: {
            required: true,
            choiceQuestion: {
              type: 'RADIO',
              options: question.options.map(option => ({ value: option })),
              shuffle: true
            },
            grading: {
              pointValue: 1,
              correctAnswers: {
                answers: [{ value: question.correctAnswer }]
              }
            },
            questionId: `q${index + 1}`
          }
        }
      }))
    };

    return [{ json: googleFormPayload }];
  } catch (error) {
    return [{
      json: {
        error: `Failed to generate Google Form payload: ${error.message}`,
        rawResponse: llmResponse[0]?.output || null
      }
    }];
  }
}

return parseTestPaperForGoogleForm($input.all().map(item => item.json));

