function parseEmailContent(rawText) {
  const splitIndex = rawText.indexOf(',');
  if (splitIndex === -1) {
    throw new Error("Invalid input format: expected a comma separating subject and body.");
  }

  const subject = rawText.substring(0, splitIndex).trim();
  let body = rawText.substring(splitIndex + 1).trim();

  // Insert line breaks after periods followed by space for readability
  body = body.replace(/\. +/g, '.\n');

  return {
    subject,
    body
  };
}

