function cleanOptionValue(value) {
  // Remove letter prefix like "A) " or "E) " and trim
  return value.replace(/^[A-Z]\)\s*/, '').trim();
}

function mapCorrectAnswer(letter, options) {
  // Convert letter to index dynamically (A=0, B=1, ...)
  const index = letter.toUpperCase().charCodeAt(0) - 65;
  if (index < 0 || index >= options.length || !options[index]) {
    console.warn(`Warning: Invalid correct answer letter "${letter}" for options length ${options.length}`);
    return options[0] ? cleanOptionValue(options[0].value) : '';
  }
  return cleanOptionValue(options[index].value);
}

function generateGoogleFormPayload(input) {
  const questions = input.items;
  if (!questions || !Array.isArray(questions)) {
    console.warn('Warning: Input does not contain a valid items array');
    return { requests: [] };
  }

  const requests = [
    // Enable quiz settings
    {
      updateSettings: {
        settings: {
          quizSettings: { isQuiz: true }
        },
        updateMask: "quizSettings.isQuiz"
      }
    },
    // Full Name field at index 0
    {
      createItem: {
        item: {
          title: "Full Name",
          questionItem: {
            question: {
              required: true,
              textQuestion: { paragraph: false }
            }
          }
        },
        location: { index: 0 }
      }
    },
    // Email field at index 1
    {
      createItem: {
        item: {
          title: "Email Address",
          questionItem: {
            question: {
              required: true,
              textQuestion: { paragraph: false }
            }
          }
        },
        location: { index: 1 }
      }
    },
    // Mobile Number field at index 2
    {
      createItem: {
        item: {
          title: "Mobile Number",
          questionItem: {
            question: {
              required: true,
              textQuestion: { paragraph: false }
            }
          }
        },
        location: { index: 2 }
      }
    },
    // Quiz questions starting at index 3
    ...questions.map((item, index) => {
      const { title, question } = item.questionItem;
      const { choiceQuestion, grading, required } = question;
      const cleanedOptions = choiceQuestion.options.map(opt => ({
        value: cleanOptionValue(opt.value)
      }));
      const correctAnswer = mapCorrectAnswer(
        grading.correctAnswers.answers[0].value,
        choiceQuestion.options
      );

      return {
        createItem: {
          item: {
            title: title,
            questionItem: {
              question: {
                required: required,
                choiceQuestion: {
                  type: choiceQuestion.type,
                  options: cleanedOptions,
                  shuffle: choiceQuestion.shuffle
                },
                grading: {
                  pointValue: grading.pointValue,
                  correctAnswers: { answers: [{ value: correctAnswer }] }
                }
              }
            }
          },
          location: { index: index + 3 } // shift by 3 now
        }
      };
    })
  ];

  return { requests };
}

function main() {
  const firstItem = $input.first();
  const input = firstItem?.json;

  if (!input?.items || !Array.isArray(input.items)) {
    console.warn('Warning: No valid input data or items array provided');
    return [{ json: { requests: [] } }];
  }

  const payload = generateGoogleFormPayload(input);
  return [{ json: payload }];
}

return main();
